    },
    {
      id: uuidv4(),
      favorito: false,
      nome: "BELLE",
      cargo: "Tiro Preciso",
      imagem:
        "https://www.brawlstarsdicas.com.br/wp-content/uploads/2021/04/belle-brawl-stars-dicas.png",
      time: times[2].nome,
    },
    {
      id: uuidv4(),
      favorito: false,
      nome: "TARA",
      cargo: "Destruidor",
      imagem:
        "https://www.brawlstarsdicas.com.br/wp-content/uploads/2018/07/tara-brawl-stars-dicas-estrategias-wiki.png",
      time: times[3].nome,
    },
    {
      id: uuidv4(),
      favorito: false,
      nome: "BYRON",
      cargo: "Suporte",
      imagem:
        "https://www.brawlstarsdicas.com.br/wp-content/uploads/2020/12/byron-brawl-stars-dicas.png",
      time: times[3].nome,
    },
    {
      id: uuidv4(),
      favorito: false,
      nome: "SQUEAK",
      cargo: "Suporte",
      imagem:
        "https://www.brawlstarsdicas.com.br/wp-content/uploads/2021/04/squeak-brawl-stars-dicas.png",
      time: times[3].nome,
    },
    {
      id: uuidv4(),
      favorito: false,
      nome: "FANG",
      cargo: "Algoz",
      imagem:
        "https://www.brawlstarsdicas.com.br/wp-content/uploads/2021/12/fang-icone-brawl-stars-dicas.png",
      time: times[3].nome,
    },
    {
      id: uuidv4(),
      favorito: false,
      nome: "CORVO",
      cargo: "Algoz",
      imagem:
        "https://www.brawlstarsdicas.com.br/wp-content/uploads/2017/06/corvo-brawl-stars-dicas.png",
      time: times[4].nome,
    },
    {
      id: uuidv4(),
      favorito: false,
      nome: "DANIEL ARTINE",
      cargo: "Engenheiro de Software na Stone Age",
      imagem:
        "https://www.alura.com.br/assets/img/lideres/daniel-artine.1647533644.jpeg",
      time: times[4].nome,
    },
    {
      id: uuidv4(),
      favorito: false,
      nome: "GUILHERME LIMA",
      cargo: "Desenvolvedor Python e JavaScript na Alura",
      imagem:
        "	https://www.alura.com.br/assets/img/lideres/guilherme-lima.1647533644.jpeg",
      time: times[4].nome,
    },
    {
      id: uuidv4(),
      favorito: false,
      nome: "PAULO SILVEIRA",
      cargo: "Hipster e CEO da Alura",
      imagem:
        "https://www.alura.com.br/assets/img/lideres/paulo-silveira.1647533644.jpeg",
      time: times[4].nome,
    },
  ];

  const [colaboradores, setColaboradores] = useState(inicial);

  const cadastrarTime = (novoTime) => {
    setTimes([...times, { ...novoTime, id: uuidv4() }]);
  };

  const mudarCorDoTime = (cor, id) => {
    setTimes(
      times.map((time) => {
        if (time.id === id) {
          time.cor = cor;
        }
        return time;
      })
    );
  };

  const resolverFavorito = (id) => {
    setColaboradores(
      colaboradores.map((colaborador) => {
        if (colaborador.id === id) colaborador.favorito = !colaborador.favorito;
        return colaborador;
      })
    );
  };

  const deletarColaborador = (id) => {
    setColaboradores(
      colaboradores.filter((colaborador) => colaborador.id !== id)
    );
  };
  return (
    <div>
      <Banner />
      <Formulario
        cadastrarTime={cadastrarTime}
        times={times.map((time) => time.nome)}
        aoCadastrar={(colaborador) =>
          setColaboradores([...colaboradores, { ...colaborador, id: uuidv4() }])
        }
      />
      <section className="times">
        <h1>Minha organização</h1>
        {times.map((time, indice) => (
          <Time
            mudarCor={mudarCorDoTime}
            key={indice}
            time={time}
            colaboradores={colaboradores.filter(
              (colaborador) => colaborador.time === time.nome
            )}
            aoDeletar={deletarColaborador}
            aoFavoritar={resolverFavorito}
          />
        ))}
      </section>
      <Rodape />
    </div>
  );
}

export default App;
